import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'ToDO';
  list: any[] = [];
  AddTask(item: string) {

    this.list.push({ id: this.list.length, name: item, status: 'Not Done' });
    console.warn(this.list);
  }
  DeleteItem(id: number) {
    console.warn(id);
    this.list = this.list.filter(item => item.id !== id)
  }
  Done(id: number) {
    this.list.filter(e => {
      if (e.id = id) {
        e.status = 'done';
        return e;
      }
    })
  }


}
